// store/profile.js
const state = {
    profile: [],
  };

  const getters = {
    profile: (state) => state.profile,
  };

  const actions = {
    fetchProfile: async ({ commit, rootGetters }) => {
      try {
        const token = rootGetters['authToken'];

        // Check if the token is available

        if (!token) {
          console.error('Token not available');
          return;
        }

        const response = await fetch('http://127.0.0.1:8000/api/profile', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
        });

        const responseData = await response.json();

        if (!response.ok) {
          throw new Error(responseData.message || 'Failed to fetch albums');
        }

        // Commit the fetched albums to the Vuex store
        commit('setProfile', responseData.data);

      } catch (error) {
        console.error('Error fetching albums:', error.message);
      }
    },
  };

  const mutations = {
    setAlbums: (state, profile) => {
      state.profile = profile;
    },
  };

  export default {
    state,
    getters,
    actions,
    mutations,
  };
