// store/auth.js
const state = {
    token: localStorage.getItem('token') || '',
    status: '',
  };

  const getters = {
    isLoggedIn: (state) => !!state.token,
    authStatus: (state) => state.status,
    authToken: (state) => state.token,
  };

  const actions = {
    login: async ({ commit }, credentials) => {
      try {
        const response = await fetch('http://127.0.0.1:8000/api/login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(credentials),
        });

        const responseData = await response.json();

        if (!response.ok) {
          throw new Error(responseData.message || 'Failed to login');
        }

        const token = responseData?.data?.authorization?.token;
        localStorage.setItem('token', token);
        commit('loginSuccess', token);

        // Return the success response to handle routing in the Vue component
        return { success: true };
      } catch (error) {
        commit('loginFailure', error.message);

        // Return the error response to handle routing in the Vue component
        return { success: false, message: error.message };
      }
    },
    logout: ({ commit }) => {
      localStorage.removeItem('token');
      commit('logout');
    },
  };

  const mutations = {
    loginSuccess: (state, token) => {
      state.token = token;
      state.status = 'success';
    },
    loginFailure: (state, errorMessage) => {
      state.token = '';
      state.status = errorMessage;
    },
    logout: (state) => {
      state.token = '';
    },
  };

  export default {
    state,
    getters,
    actions,
    mutations,
  };
