// store/auth.js
const state = {
  token: localStorage.getItem('token') || '',
  status: '',
};

const getters = {
  isLoggedIn: (state) => !!state.token,
  authStatus: (state) => state.status,
  authToken: (state) => state.token,
};

const actions = {
  register: async ({ commit }, userData) => {
    try {
      fetch('http://127.0.0.1:8000/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      })
        .then((response) => {
          return response.json();
        })
        .then((responseData) => {
          // Handle the user data or perform additional actions
          console.log('User successfully registered:', responseData);

          if(responseData.data.success) {
            commit('SET_USER', responseData.data.user);

          }
          else {
            throw new Error(responseData.data.message);
          }

        })
        .catch((error) => {
          // Handle errors during the fetch or processing
          console.error('Error during registration:', error.message);

          throw new Error( error.message);
        });



      // Handle successful registration response
    } catch (error) {
      throw new Error(error);
    }
  },
  login: async ({ commit }, credentials) => {
    try {
      const response = await fetch('http://127.0.0.1:8000/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(credentials),
      });

      const responseData = await response.json();

      if (!response.ok) {
        throw new Error(responseData.message || 'Failed to login');
      }

      const token = responseData?.data?.authorization?.token;
      localStorage.setItem('token', token);
      commit('loginSuccess', token);

      // Return the success response to handle routing in the Vue component
      return { success: true };
    } catch (error) {
      commit('loginFailure', error.message);

      // Return the error response to handle routing in the Vue component
      return { success: false, message: error.message };
    }
  },
  logout: ({ commit }) => {
    localStorage.removeItem('token');
    commit('logout');
  },
};

const mutations = {
  loginSuccess: (state, token) => {
    state.token = token;
    state.status = 'success';
  },
  loginFailure: (state, errorMessage) => {
    state.token = '';
    state.status = errorMessage;
  },
  logout: (state) => {
    state.token = '';
  },
  SET_USER(state, user) {
    state.user = user;
  },
};

export default {
  state,
  getters,
  actions,
  mutations,
};
